<?phprequire_once 'db.php';$db = new Database();$conn = $db->openDB();if ($conn == null){	die();}$query = "SELECT guestid, guestname, isconfirmed from guest where guestpassword = :guestpassword limit 1;";$stmt = $conn->prepare($query);$guestpassword = $_POST['guestpassword'];$stmt->bindParam('guestpassword', $guestpassword);$stmt->execute();$row = $stmt->fetch();$loginSuccessful = $stmt->rowCount() == 1;$db->closeDB();if ($loginSuccessful && session_start()){	$_SESSION['isLoggedIn'] = $loginSuccessful;	$_SESSION['guestid'] = $row['guestid'];	$_SESSION['isconfirmed'] = $row['isconfirmed'];	$_SESSION['guestname'] = $row['guestname'];	header('location:../welcome.php');}else{	header('location:../index.php');}?>